import React from 'react';
import './About.css'; 

// --- IMPORT IMAGES ---
// Ensure your images are in src/assets/ folder
import founder1 from '../assets/founder1.jpg';
import founder2 from '../assets/founder2.jpg';
import founder3 from '../assets/founder3.jpg';
import founder4 from '../assets/founder4.jpg';

const About = () => {
  // Founder Data Array
  const founders = [
    {
      id: 1,
      name: 'Nithish',
      role: 'CEO & Founder',
      image: founder1,
      bio: 'Visionary leader focused on revolutionizing urban mobility in Chennai.'
    },
    {
      id: 2,
      name: 'Co-Founder Name',
      role: 'Chief Technology Officer',
      image: founder2,
      bio: 'Tech enthusiast ensuring our platform runs smoothly 24/7.'
    },
    {
      id: 3,
      name: 'Co-Founder Name',
      role: 'Head of Operations',
      image: founder3,
      bio: 'Expert in logistics and fleet management, keeping cars ready for you.'
    },
    {
      id: 4,
      name: 'Co-Founder Name',
      role: 'Marketing Director',
      image: founder4,
      bio: 'Connecting people with the joy of driving through creative campaigns.'
    }
  ];

  return (
    <div className="about-container">
      {/* Hero Section */}
      <div className="about-hero">
        <h1>About AutoRent</h1>
        <p>Driving the future of car rentals, one journey at a time.</p>
      </div>

      {/* Mission Section */}
      <div className="mission-section">
        <div className="mission-content">
          <h2>Our Mission</h2>
          <p>
            At AutoRent, we believe that mobility should be accessible, affordable, and 
            hassle-free. We started with a simple idea: to provide high-quality vehicles 
            without the complex paperwork and hidden fees of traditional rental agencies.
          </p>
        </div>
      </div>

      {/* Founders Grid */}
      <div className="founders-section">
        <h2>Meet the Team</h2>
        <div className="founders-grid">
          {founders.map((founder) => (
            <div className="founder-card" key={founder.id}>
              <div className="image-wrapper">
                <img src={founder.image} alt={founder.name} />
              </div>
              <h3>{founder.name}</h3>
              <p className="role">{founder.role}</p>
              <p className="bio">{founder.bio}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default About;
