import React, { createContext, useState, useEffect, useContext } from 'react';
import api from '../api/axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check for existing session on load
  useEffect(() => {
    const storedUser = sessionStorage.getItem('carRentalUser');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setLoading(false);
  }, []);

  // Login Function
  const login = async (email, password) => {
    try {
      // JSON Server supports filtering. This mimics a backend DB query.
      const response = await api.get(`/users?email=${email}&password=${password}`);
      
      if (response.data.length > 0) {
        const loggedUser = response.data[0];
        // Remove password before storing in state/session for safety
        const { password, ...userWithoutPass } = loggedUser;
        
        setUser(userWithoutPass);
        sessionStorage.setItem('carRentalUser', JSON.stringify(userWithoutPass));
        return { success: true };
      } else {
        return { success: false, message: 'Invalid credentials' };
      }
    } catch (error) {
      return { success: false, message: 'Server error' };
    }
  };

  // Register Function
  const register = async (userData) => {
    try {
      // Check if email exists
      const existing = await api.get(`/users?email=${userData.email}`);
      if (existing.data.length > 0) {
        return { success: false, message: 'Email already in use' };
      }

      // Add default role and basic data
      const newUser = {
        ...userData,
        role: 'user',
        loyaltyPoints: 0,
        createdAt: new Date().toISOString()
      };

      const response = await api.post('/users', newUser);
      
      // Auto-login after register
      const { password, ...userWithoutPass } = response.data;
      setUser(userWithoutPass);
      sessionStorage.setItem('carRentalUser', JSON.stringify(userWithoutPass));
      
      return { success: true };
    } catch (error) {
      return { success: false, message: 'Registration failed' };
    }
  };

  const logout = () => {
    setUser(null);
    sessionStorage.removeItem('carRentalUser');
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, register, loading }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
